import{_ as e,b as t,c as o,d as l}from"./app.D-A8O3CX.js";import"./chunks/vendor.8Zb1yHKv.js";import"./chunks/vendor-mermaid.BZfVRecf.js";const _=JSON.parse('{"title":"SkyElytra（幻空翼飞行系统）插件文档","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"develop/mc_plugins/SkyElytra.md","filePath":"develop/mc_plugins/SkyElytra.md","lastUpdated":1770199244000}'),r={name:"develop/mc_plugins/SkyElytra.md"};function i(d,a,n,s,h,c){return t(),o("div",null,[...a[0]||(a[0]=[l('<h1 id="skyelytra-幻空翼飞行系统-插件文档" tabindex="-1">SkyElytra（幻空翼飞行系统）插件文档 <a class="header-anchor" href="#skyelytra-幻空翼飞行系统-插件文档" aria-label="Permalink to &quot;SkyElytra（幻空翼飞行系统）插件文档&quot;">​</a></h1><h3 id="核心定位" tabindex="-1">核心定位 <a class="header-anchor" href="#核心定位" aria-label="Permalink to &quot;核心定位&quot;">​</a></h3><p>为Paper1.21.11服务端打造的<strong>精细化飞行管理插件</strong>，针对<strong>建筑玩家</strong>实现领地内平衡可控的无限飞行体验，为<strong>冒险玩家</strong>提供幻空翼、经验双模式限时飞行功能，粒子特效。</p><h3 id="插件基础信息" tabindex="-1">插件基础信息 <a class="header-anchor" href="#插件基础信息" aria-label="Permalink to &quot;插件基础信息&quot;">​</a></h3><table tabindex="0"><thead><tr><th>项⽬</th><th>详情</th></tr></thead><tbody><tr><td>主命令</td><td><code>/skyelytra</code>（别名：<code>/fly</code>、<code>/se</code>，支持指令自动补全）</td></tr><tr><td>作者</td><td>F.windEmiko（狐风轩汐）</td></tr><tr><td>服务端兼容</td><td>Paper 1.21.8+（Spigot兼容）</td></tr><tr><td>开发环境</td><td>Java 21</td></tr><tr><td>数据库</td><td>SQLite（支持MySQL）</td></tr><tr><td>挂钩插件（均为软依赖，模块设计）</td><td>LuckPerms、PlaceholderAPI、WorldGuard、dominion</td></tr></tbody></table><h2 id="一、快速上手" tabindex="-1">一、快速上手 <a class="header-anchor" href="#一、快速上手" aria-label="Permalink to &quot;一、快速上手&quot;">​</a></h2><h3 id="_1-1-基础配置" tabindex="-1">1.1 基础配置 <a class="header-anchor" href="#_1-1-基础配置" aria-label="Permalink to &quot;1.1 基础配置&quot;">​</a></h3><p>插件配置目录包含<code>config.yml</code>等多模块配置文件，合理设计、<code>messages.yml</code>（消息自定义）、<code>data/</code>（数据库文件）、<code>data/cache</code>（缓存文件[如果有]）；</p><h3 id="_1-2-核心子命令操作" tabindex="-1">1.2 核心子命令操作 <a class="header-anchor" href="#_1-2-核心子命令操作" aria-label="Permalink to &quot;1.2 核心子命令操作&quot;">​</a></h3><ul><li>查看自身或他人的飞行信息：<code>info &lt;player&gt;</code>；权限：默认可查看自己，op或权限可查看他人<code>skyelytra.admin.info</code></li><li>切换限时飞行模式(幻空翼/经验值/关闭)：<code>toggle skyelytra/exp/none</code>；权限（默认）：<code>skyelytra.toggle.*</code></li><li>设置无限飞行（无限时间和不消耗经验值）：<code>infinite skyelytra/exp &lt;player&gt;</code>；权限(op)：<code>skyelytra.admin.infinite</code></li><li>切换剩余时间显示方式（BOSS栏和活动栏）：<code>show actionbar/bossbar</code>；权限（默认）：<code>skyelytra.show.*</code></li><li>管理员重载配置：<code>reload</code>；权限(op)：<code>skyelytra.admin.reload</code></li><li>管理员修改玩家飞行时间（玩家支持补全）：<code>time add/remove/set &lt;玩家名&gt; &lt;秒数&gt;</code>；权限(op)：<code>skyelytra.admin.time</code></li></ul><h2 id="二、核心功能详解" tabindex="-1">二、核心功能详解 <a class="header-anchor" href="#二、核心功能详解" aria-label="Permalink to &quot;二、核心功能详解&quot;">​</a></h2><h3 id="_2-1-领地专属飞行体系" tabindex="-1">2.1 领地专属飞行体系 <a class="header-anchor" href="#_2-1-领地专属飞行体系" aria-label="Permalink to &quot;2.1 领地专属飞行体系&quot;">​</a></h3><p>挂钩<strong>dominion领地插件</strong>：</p><ol><li>玩家仅在<strong>自身领地</strong>或<strong>被添加为信任成员的他人领地</strong>内，可无限飞行；</li><li>领地边界机制：离开领地范围时，<strong>未开启限时飞行功能</strong>则立即触发坠落，<strong>已开启幻空翼/经验飞行</strong>则自动切换为对应限时飞行模式（就是在领地内不会扣时间或经验值）；</li><li>领地飞行范围不受禁飞区限制</li></ol><h3 id="_2-2-双模式限时飞行" tabindex="-1">2.2 双模式限时飞行 <a class="header-anchor" href="#_2-2-双模式限时飞行" aria-label="Permalink to &quot;2.2 双模式限时飞行&quot;">​</a></h3><h4 id="_2-2-1-幻空翼限时飞行" tabindex="-1">2.2.1 幻空翼限时飞行 <a class="header-anchor" href="#_2-2-1-幻空翼限时飞行" aria-label="Permalink to &quot;2.2.1 幻空翼限时飞行&quot;">​</a></h4><ul><li>飞行时长存在最大飞行时间限制，不同权限组可单独设置上限；</li><li>新玩家初始飞行时长支持自定义配置；</li><li>管理员可通过<code>time</code>子指令修改玩家剩余时长；</li><li>时长消耗仅在<strong>实际飞行状态</strong>下触发，落地/取消飞行则停止计时；</li><li><strong>粒子特效</strong>：飞行时玩家脚底生成粒子特效，粒子效果种类支持配置，默认为绿色小粒子，支持不同权限组（会员）设置不同粒子；</li></ul><h4 id="_2-2-2-经验飞行" tabindex="-1">2.2.2 经验飞行 <a class="header-anchor" href="#_2-2-2-经验飞行" aria-label="Permalink to &quot;2.2.2 经验飞行&quot;">​</a></h4><ul><li>消耗玩家经验值的动态飞行模式，每秒消耗经验值数量支持自定义配置，不同权限组支持单独配置；</li><li>经验不足时自动取消飞行并触发坠落；</li><li>与幻空翼飞行模式无法同时开启，通过<code>toggle exp</code>无缝切换。</li></ul><h3 id="_2-5-可视化显示控制" tabindex="-1">2.5 可视化显示控制 <a class="header-anchor" href="#_2-5-可视化显示控制" aria-label="Permalink to &quot;2.5 可视化显示控制&quot;">​</a></h3><p>飞行剩余时长支持<strong>双模式显示</strong>，，显示内容可在消息配置中可自定义：</p><ol><li>动作栏（Actionbar）；</li><li>老板栏（Bossbar）；</li><li>格式自定义：支持<code>%时%分%秒</code>等占位符，可配置；</li></ol><h3 id="_2-6-飞行状态保存" tabindex="-1">2.6 飞行状态保存 <a class="header-anchor" href="#_2-6-飞行状态保存" aria-label="Permalink to &quot;2.6 飞行状态保存&quot;">​</a></h3><ol><li>保存内容：包含当前飞行模式（幻空翼/经验）、剩余时间显示载体（动作栏/老板栏）、无限飞行状态（开启/关闭）、飞行功能开关状态（飞行中/未飞行）；</li><li>当玩家正常退出服务器、服务器重启/重载、手动切换飞行状态时记录；</li><li>当玩家重新登录服务器等，重新开启飞行功能，避免频繁开关操作；</li><li>该功能可配置是否开启，默认开启。</li></ol><h3 id="_2-7-摔落伤害与坠落条件" tabindex="-1">2.7 摔落伤害与坠落条件 <a class="header-anchor" href="#_2-7-摔落伤害与坠落条件" aria-label="Permalink to &quot;2.7 摔落伤害与坠落条件&quot;">​</a></h3><p>保障服务器战斗公平性，所有规则可配置</p><h4 id="_2-7-1-战斗坠落机制" tabindex="-1">2.7.1 战斗坠落机制 <a class="header-anchor" href="#_2-7-1-战斗坠落机制" aria-label="Permalink to &quot;2.7.1 战斗坠落机制&quot;">​</a></h4><p>飞行中战斗行为触发强制坠落：</p><ol><li>玩家在飞行状态下<strong>主动攻击任意实体</strong>或<strong>受到任意实体攻击</strong>，立即触发坠落；</li><li>一段时间后（默认10秒）自动恢复飞行状态，可配置；</li></ol><h4 id="_2-7-2-重锤坠落机制" tabindex="-1">2.7.2 重锤坠落机制 <a class="header-anchor" href="#_2-7-2-重锤坠落机制" aria-label="Permalink to &quot;2.7.2 重锤坠落机制&quot;">​</a></h4><p>支持配置重锤是否在拿起重锤时无法飞行和立即坠落，以及在坠落图中是否可以造成伤害；</p><h4 id="_2-7-3-飞行高度限制" tabindex="-1">2.7.3 飞行高度限制 <a class="header-anchor" href="#_2-7-3-飞行高度限制" aria-label="Permalink to &quot;2.7.3 飞行高度限制&quot;">​</a></h4><p>可在配置中自定义飞行高度上下限，支持不同权限组独立配置</p><h4 id="_2-7-4-禁飞区域" tabindex="-1">2.7.4 禁飞区域 <a class="header-anchor" href="#_2-7-4-禁飞区域" aria-label="Permalink to &quot;2.7.4 禁飞区域&quot;">​</a></h4><p>挂钩WorldGuard插件</p><ol><li>当玩家进入配置的禁飞区域（WorldGuard插件中定义的区域名），立即触发坠落；</li><li>领地飞行无视禁飞区域；</li></ol><h4 id="_2-7-5-摔落伤害" tabindex="-1">2.7.5 摔落伤害 <a class="header-anchor" href="#_2-7-5-摔落伤害" aria-label="Permalink to &quot;2.7.5 摔落伤害&quot;">​</a></h4><ol><li>启用了飞行功能，但没有进入飞行状态时，从高处跳下会正常收到摔落伤害，可配置</li><li>可配置战斗行为坠落是否造成伤害</li><li>进入禁飞区域坠落是否造成摔落伤害</li></ol><p>调试模式：支持开启debug模式输出详细运行日志</p><p>占位符格式：可自定义PlaceholderAPI（PAPI）占位符的时间显示格式（如%时%分%秒），是否在飞行状态，可在消息配置中自定义</p><p>独立的messages.yml配置文件，全消息文本支持颜色格式化码，兼容原版游戏的&amp;和minimassage格式 默认消息采用「简约美观的渐变配色 + 白话文风格」，可全量自定义</p><p>完善的权限系统</p><p>使用模块化思维开发，分类</p>',43)])])}const b=e(r,[["render",i]]);export{_ as __pageData,b as default};
