import{_ as l,c as e,b as i,d as o}from"./app.CWaWVY-8.js";import"./chunks/vendor.m4U5NtMm.js";import"./chunks/vendor-mermaid.DkiWj-td.js";const _=JSON.parse('{"title":"SkyElytra（幻空翼飞行系统）","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"develop/mc_plugins/SkyElytra.md","filePath":"develop/mc_plugins/SkyElytra.md","lastUpdated":1769182817000}'),r={name:"develop/mc_plugins/SkyElytra.md"};function t(d,a,n,s,c,h){return i(),e("div",null,[...a[0]||(a[0]=[o('<h1 id="skyelytra-幻空翼飞行系统" tabindex="-1">SkyElytra（幻空翼飞行系统） <a class="header-anchor" href="#skyelytra-幻空翼飞行系统" aria-label="Permalink to &quot;SkyElytra（幻空翼飞行系统）&quot;">​</a></h1><h2 id="项目状态-a" tabindex="-1">项目状态：A <a class="header-anchor" href="#项目状态-a" aria-label="Permalink to &quot;项目状态：A&quot;">​</a></h2><p>基于 Paper API 1.21.8 开发的 Java 插件，旨在为建筑玩家提供领地内平衡、舒适且可控的限时飞行体验，为冒险玩家提供趣味性兼顾实用的鞘翅弹射滑翔飞行功能。</p><h3 id="基本信息" tabindex="-1">基本信息 <a class="header-anchor" href="#基本信息" aria-label="Permalink to &quot;基本信息&quot;">​</a></h3><ul><li><strong>插件版本</strong>：0.1</li><li><strong>插件命令</strong>：<code>/skyelytra</code>（别名：<code>/fly</code> <code>/se</code>）</li><li><strong>作者</strong>：F.windEmiko（狐风轩汐）</li></ul><h3 id="主要功能" tabindex="-1">主要功能 <a class="header-anchor" href="#主要功能" aria-label="Permalink to &quot;主要功能&quot;">​</a></h3><h4 id="_1-分级权限指令系统" tabindex="-1">1. 分级权限指令系统 <a class="header-anchor" href="#_1-分级权限指令系统" aria-label="Permalink to &quot;1. 分级权限指令系统&quot;">​</a></h4><p>支持指令自动补全，无对应权限则指令不显示，核心子命令设计如下：</p><ul><li><code>help</code>：查询插件所有可用指令帮助（无权限则不显示）</li><li><code>info [player]</code>：查看玩家飞行信息（剩余时间、show显示状态、是否绑定了幻空翼(鞘翅)、无限飞行状态），默认可查自身，查询他人需<code>skyelytra.admin.info</code>权限</li><li><code>time [add/remove/set] [player] [second]</code>：修改玩家剩余飞行时间，需<code>skyelytra.admin.time.*</code>权限</li><li><code>infinite</code>：启用/关闭无限飞行特权，需<code>skyelytra.infinite</code>权限</li><li><code>show [actionbar/bossbar]</code>：切换飞行剩余时间的显示载体（动作栏/老板栏），需<code>skyelytra.show.*</code>权限</li><li><code>bind [player]</code>：绑定玩家手上的鞘翅为自己的幻空翼</li><li><code>reload</code>：重载插件所有配置文件，需<code>skyelytra.admin.reload</code>权限</li></ul><h4 id="_2-领地限时飞行模块" tabindex="-1">2. 领地限时飞行模块 <a class="header-anchor" href="#_2-领地限时飞行模块" aria-label="Permalink to &quot;2. 领地限时飞行模块&quot;">​</a></h4><ul><li><strong>挂钩dominion领地插件</strong>：仅当玩家位于 自己的领地或作为别人领地的信任成员 时，可开启限时飞行</li><li><strong>离开领地坠落机制</strong>：当离开领地范围时会坠落</li><li><strong>区域限制机制</strong>：进入WorldGuard标记的禁飞区域立即坠落</li></ul><h4 id="_3-幻空翼鞘翅绑定模块" tabindex="-1">3. 幻空翼鞘翅绑定模块 <a class="header-anchor" href="#_3-幻空翼鞘翅绑定模块" aria-label="Permalink to &quot;3. 幻空翼鞘翅绑定模块&quot;">​</a></h4><ul><li>可打开一个兑换界面(箱子界面)，使用一个满耐久没有任何附魔的鞘翅，结合config配置中定义的转换额外开销（Vault货币、经验值、原版物品），转换成新的幻空翼，其名字和lora均可在massage配置中自定义，且支持custom_model_data通过其他材质自定义材质。</li><li>待设计</li></ul><h4 id="_3-多维度配置自定义" tabindex="-1">3. 多维度配置自定义 <a class="header-anchor" href="#_3-多维度配置自定义" aria-label="Permalink to &quot;3. 多维度配置自定义&quot;">​</a></h4><ul><li>摔落伤害配置：可分别设置「攻击/受击坠落」「正常取消飞行坠落」「离开领地范围」是否触发摔落伤害</li><li>受击豁免配置：可设置是否排除幻翼攻击导致的坠落</li><li>初始时间配置：自定义新玩家默认拥有的飞行时长</li><li>调试模式：支持开启debug模式输出详细运行日志</li><li>占位符格式：可自定义PlaceholderAPI（PAPI）占位符的时间显示格式（如%时%分%秒）</li></ul><h4 id="_4-数据与生态兼容" tabindex="-1">4. 数据与生态兼容 <a class="header-anchor" href="#_4-数据与生态兼容" aria-label="Permalink to &quot;4. 数据与生态兼容&quot;">​</a></h4><ul><li>双数据库支持：集成MySQL和SQLite，适配不同服务器部署需求</li><li>跨服数据同步：内置缓存机制，保障跨服场景下数据可靠性与访问性能</li><li>独立的文件夹里存放数据和缓存文件</li><li>插件深度集成：对接PlaceholderAPI（占位符）、LuckPerms（权限）、WorldGuard（区域管理）、Vault（经济）、dominion（领地）五大核心插件</li></ul><h3 id="核心占位符支持-placeholderapi" tabindex="-1">核心占位符支持（PlaceholderAPI） <a class="header-anchor" href="#核心占位符支持-placeholderapi" aria-label="Permalink to &quot;核心占位符支持（PlaceholderAPI）&quot;">​</a></h3><ul><li><code>%skyelytra_time%</code>：显示玩家剩余飞行时间（格式可自定义）</li><li><code>%skyelytra_status%</code>：显示玩家当前是否处于飞行状态</li></ul><h3 id="消息文本自定义" tabindex="-1">消息文本自定义 <a class="header-anchor" href="#消息文本自定义" aria-label="Permalink to &quot;消息文本自定义&quot;">​</a></h3><ul><li>独立的<code>messages.yml</code>配置文件，全消息文本支持颜色格式化码</li><li>默认消息采用「简约美观的华丽配色 + 白话文风格」，可全量自定义</li></ul><h3 id="技术特点" tabindex="-1">技术特点 <a class="header-anchor" href="#技术特点" aria-label="Permalink to &quot;技术特点&quot;">​</a></h3><ul><li><strong>开发环境</strong>：Java 21 开发，适配 Paper 1.21.6+ 服务端</li><li><strong>权限控制</strong>：基于LuckPerms实现精细化权限管理，指令可见性与权限强绑定</li><li><strong>区域管理</strong>：深度集成WorldGuard，精准识别禁飞区域边界</li><li><strong>性能优化</strong>：内置数据缓存机制，减少数据库交互频次，提升响应速度</li><li><strong>兼容性</strong>：全功能适配我的世界Java版1.21.6+，无核心功能冲突</li></ul>',23)])])}const k=l(r,[["render",t]]);export{_ as __pageData,k as default};
