import{_ as l,b as a,c as i,d as o}from"./app.DiX3nUp-.js";import"./chunks/vendor.8Zb1yHKv.js";import"./chunks/vendor-mermaid.jBnhHIAT.js";const m=JSON.parse('{"title":"FE_Fly（客制化飞行插件）","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"develop/mc_plugins/fe_fly.md","filePath":"develop/mc_plugins/fe_fly.md","lastUpdated":1765920788000}'),r={name:"develop/mc_plugins/fe_fly.md"};function t(d,e,n,c,s,h){return a(),i("div",null,[...e[0]||(e[0]=[o('<h1 id="fe-fly-客制化飞行插件" tabindex="-1">FE_Fly（客制化飞行插件） <a class="header-anchor" href="#fe-fly-客制化飞行插件" aria-label="Permalink to &quot;FE_Fly（客制化飞行插件）&quot;">​</a></h1><h2 id="项目状态-d" tabindex="-1">项目状态：D <a class="header-anchor" href="#项目状态-d" aria-label="Permalink to &quot;项目状态：D&quot;">​</a></h2><p>基于 Paper API 1.21.6+ 开发的 Java 插件，旨在为玩家提供平衡、舒适且可控的限时飞行体验，兼顾趣味性与服务器生态平衡。</p><h3 id="基本信息" tabindex="-1">基本信息 <a class="header-anchor" href="#基本信息" aria-label="Permalink to &quot;基本信息&quot;">​</a></h3><ul><li><strong>插件版本</strong>：1.2.0</li><li><strong>插件命令</strong>：<code>/fefly</code>（别名：<code>/fly</code>）</li><li><strong>作者</strong>：F.windEmiko（狐风轩汐）</li></ul><h3 id="主要功能" tabindex="-1">主要功能 <a class="header-anchor" href="#主要功能" aria-label="Permalink to &quot;主要功能&quot;">​</a></h3><h4 id="_1-分级权限指令系统" tabindex="-1">1. 分级权限指令系统 <a class="header-anchor" href="#_1-分级权限指令系统" aria-label="Permalink to &quot;1. 分级权限指令系统&quot;">​</a></h4><p>支持指令自动补全，无对应权限则指令不显示，核心子命令设计如下：</p><ul><li><code>help</code>：查询插件所有可用指令帮助（无权限则不显示）</li><li><code>info [player]</code>：查看玩家飞行信息（剩余时间、显示状态、无限飞行状态），默认可查自身，查询他人需<code>fefly.admin.info</code>权限</li><li><code>time [add/remove/set] [player] [second]</code>：修改玩家剩余飞行时间，需<code>fefly.admin.time.*</code>权限</li><li><code>infinite</code>：启用/关闭无限飞行特权，需<code>fefly.infinite</code>权限</li><li><code>show [actionbar/bossbar]</code>：切换飞行剩余时间的显示载体（动作栏/老板栏），需<code>fefly.show.*</code>权限</li><li><code>reload</code>：重载插件所有配置文件，需<code>fefly.admin.reload</code>权限</li></ul><h4 id="_2-动态飞行限制规则" tabindex="-1">2. 动态飞行限制规则 <a class="header-anchor" href="#_2-动态飞行限制规则" aria-label="Permalink to &quot;2. 动态飞行限制规则&quot;">​</a></h4><ul><li><strong>战斗坠落机制</strong>：飞行中攻击实体/受到攻击时立即取消飞行并坠落，指定时间后恢复飞行（恢复时长可配置，默认10秒）</li><li><strong>重锤坠落机制</strong>：飞行时手持重锤会因重量坠落，伴随专属提示消息（是否启用可配置）</li><li><strong>区域限制机制</strong>：进入WorldGuard标记的禁飞区域立即坠落</li><li><strong>高度限制机制</strong>：可自定义飞行的最低/最高高度（格数）</li></ul><h4 id="_3-多维度配置自定义" tabindex="-1">3. 多维度配置自定义 <a class="header-anchor" href="#_3-多维度配置自定义" aria-label="Permalink to &quot;3. 多维度配置自定义&quot;">​</a></h4><ul><li>摔落伤害配置：可分别设置「攻击/受击坠落」「正常取消飞行坠落」是否触发摔落伤害</li><li>受击豁免配置：可设置是否排除幻翼攻击导致的坠落</li><li>初始时间配置：自定义新玩家默认拥有的飞行时长</li><li>调试模式：支持开启debug模式输出详细运行日志</li><li>占位符格式：可自定义PlaceholderAPI（PAPI）占位符的时间显示格式（如%时%分%秒）</li></ul><h4 id="_4-数据与生态兼容" tabindex="-1">4. 数据与生态兼容 <a class="header-anchor" href="#_4-数据与生态兼容" aria-label="Permalink to &quot;4. 数据与生态兼容&quot;">​</a></h4><ul><li>双数据库支持：集成MySQL和SQLite，适配不同服务器部署需求</li><li>跨服数据同步：内置缓存机制，保障跨服场景下数据可靠性与访问性能</li><li>插件深度集成：对接PlaceholderAPI（占位符）、LuckPerms（权限）、WorldGuard（区域管理）三大核心插件</li></ul><h3 id="核心占位符支持-placeholderapi" tabindex="-1">核心占位符支持（PlaceholderAPI） <a class="header-anchor" href="#核心占位符支持-placeholderapi" aria-label="Permalink to &quot;核心占位符支持（PlaceholderAPI）&quot;">​</a></h3><ul><li><code>%fefly_time%</code>：显示玩家剩余飞行时间（格式可自定义）</li><li><code>%fefly_status%</code>：显示玩家当前是否处于飞行状态</li></ul><h3 id="消息文本自定义" tabindex="-1">消息文本自定义 <a class="header-anchor" href="#消息文本自定义" aria-label="Permalink to &quot;消息文本自定义&quot;">​</a></h3><ul><li>独立的<code>messages.yml</code>配置文件，全消息文本支持颜色格式化码</li><li>默认消息采用「简约美观的华丽配色 + 白话文风格」，可全量自定义</li></ul><h3 id="技术特点" tabindex="-1">技术特点 <a class="header-anchor" href="#技术特点" aria-label="Permalink to &quot;技术特点&quot;">​</a></h3><ul><li><strong>开发环境</strong>：Java 21 开发，适配 Paper 1.21.6+ 服务端</li><li><strong>权限控制</strong>：基于LuckPerms实现精细化权限管理，指令可见性与权限强绑定</li><li><strong>区域管理</strong>：深度集成WorldGuard，精准识别禁飞区域边界</li><li><strong>性能优化</strong>：内置数据缓存机制，减少数据库交互频次，提升响应速度</li><li><strong>兼容性</strong>：全功能适配我的世界Java版1.21.6+，无核心功能冲突</li></ul>',21)])])}const p=l(r,[["render",t]]);export{m as __pageData,p as default};
