import{_ as l,b as e,c as i,d as o}from"./app.BDlP2o-V.js";import"./chunks/vendor.8Zb1yHKv.js";import"./chunks/vendor-mermaid.CX8s52fm.js";const b=JSON.parse('{"title":"FE_Quests（冒险等级任务）插件","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"develop/mc_plugins/fe_quests.md","filePath":"develop/mc_plugins/fe_quests.md","lastUpdated":1765920788000}'),t={name:"develop/mc_plugins/fe_quests.md"};function r(s,a,n,d,h,u){return e(),i("div",null,[...a[0]||(a[0]=[o('<h1 id="fe-quests-冒险等级任务-插件" tabindex="-1">FE_Quests（冒险等级任务）插件 <a class="header-anchor" href="#fe-quests-冒险等级任务-插件" aria-label="Permalink to &quot;FE_Quests（冒险等级任务）插件&quot;">​</a></h1><h2 id="项目概述" tabindex="-1">项目概述 <a class="header-anchor" href="#项目概述" aria-label="Permalink to &quot;项目概述&quot;">​</a></h2><p>基于 Paper API 1.21.11+ 开发的 Java 插件，旨在为玩家提供教程引导，功能解锁，冒险经验等级升级的功能。</p><h2 id="插件信息" tabindex="-1">插件信息 <a class="header-anchor" href="#插件信息" aria-label="Permalink to &quot;插件信息&quot;">​</a></h2><ul><li><strong>插件版本</strong>：0.1</li><li><strong>插件命令</strong>：<code>/fequests</code>（别名：<code>/feq</code>）</li><li><strong>作者</strong>：F.windEmiko（狐风轩汐）</li></ul><h2 id="挂钩插件-依赖" tabindex="-1">挂钩插件(依赖) <a class="header-anchor" href="#挂钩插件-依赖" aria-label="Permalink to &quot;挂钩插件(依赖)&quot;">​</a></h2><ul><li>LuckPerms</li><li>PlaceholderAPI</li><li>Vault</li><li>PlayerPoints</li></ul><h2 id="主要功能" tabindex="-1">主要功能 <a class="header-anchor" href="#主要功能" aria-label="Permalink to &quot;主要功能&quot;">​</a></h2><h3 id="_1-基础新手教程部分" tabindex="-1">1. 基础新手教程部分 <a class="header-anchor" href="#_1-基础新手教程部分" aria-label="Permalink to &quot;1. 基础新手教程部分&quot;">​</a></h3><ul><li>模块化设计，待进一步开发</li></ul><h3 id="_2-冒险等级系统" tabindex="-1">2. 冒险等级系统 <a class="header-anchor" href="#_2-冒险等级系统" aria-label="Permalink to &quot;2. 冒险等级系统&quot;">​</a></h3><ul><li>独立的经验等级系统，模块化设计</li></ul><h3 id="_3-引导任务系统" tabindex="-1">3. 引导任务系统 <a class="header-anchor" href="#_3-引导任务系统" aria-label="Permalink to &quot;3. 引导任务系统&quot;">​</a></h3><ul><li>箱子GUI界面，支持任务展示和交互</li><li>丰富的任务类型和进度跟踪</li></ul><h3 id="_4-功能玩法解锁" tabindex="-1">4. 功能玩法解锁 <a class="header-anchor" href="#_4-功能玩法解锁" aria-label="Permalink to &quot;4. 功能玩法解锁&quot;">​</a></h3><ul><li>模块化设计，待进一步开发</li></ul><h3 id="_5-消息系统" tabindex="-1">5. 消息系统 <a class="header-anchor" href="#_5-消息系统" aria-label="Permalink to &quot;5. 消息系统&quot;">​</a></h3><ul><li><strong>ActionBar消息</strong>：实时显示临时信息</li><li><strong>聊天消息</strong>：标准聊天框消息</li><li><strong>BossBar消息</strong>：顶部进度条显示重要信息</li></ul><h3 id="_6-数据库系统" tabindex="-1">6. 数据库系统 <a class="header-anchor" href="#_6-数据库系统" aria-label="Permalink to &quot;6. 数据库系统&quot;">​</a></h3><ul><li><strong>SQLite支持</strong>：默认数据库，适合小型服务器</li><li><strong>MySQL支持</strong>：可选数据库，适合大型服务器</li><li><strong>自动切换</strong>：支持运行时切换数据库类型</li><li><strong>完整DAO层</strong>：提供玩家任务、等级和任务定义的数据访问对象</li></ul><h3 id="_7-模块化架构" tabindex="-1">7. 模块化架构 <a class="header-anchor" href="#_7-模块化架构" aria-label="Permalink to &quot;7. 模块化架构&quot;">​</a></h3><ul><li><strong>Core模块</strong>：提供基础模块接口</li><li><strong>Hooks模块</strong>：处理依赖插件挂钩</li><li><strong>Managers模块</strong>：管理各种系统功能</li><li><strong>Modules模块</strong>：实现具体功能模块</li><li><strong>GUI模块</strong>：处理界面系统</li><li><strong>Utils模块</strong>：提供工具类</li><li><strong>Database模块</strong>：处理数据存储</li></ul><h2 id="使用说明" tabindex="-1">使用说明 <a class="header-anchor" href="#使用说明" aria-label="Permalink to &quot;使用说明&quot;">​</a></h2><h3 id="命令" tabindex="-1">命令 <a class="header-anchor" href="#命令" aria-label="Permalink to &quot;命令&quot;">​</a></h3><ul><li><code>/fequests</code> - 打开任务GUI界面</li><li><code>/fequests help</code> - 查看帮助信息</li><li><code>/fequests version</code> - 显示插件版本</li><li><code>/fequests reload</code> - 重载插件配置（需要权限）</li><li><code>/fequests debug</code> - 切换调试模式（需要权限）</li><li><code>/fequests gui</code> - 打开任务GUI界面</li><li><code>/fequests testmessages</code> - 测试消息功能</li></ul><h3 id="权限" tabindex="-1">权限 <a class="header-anchor" href="#权限" aria-label="Permalink to &quot;权限&quot;">​</a></h3><ul><li><code>fequests.use</code> - 使用插件基本功能</li><li><code>fequests.reload</code> - 重载插件配置</li><li><code>fequests.debug</code> - 切换调试模式</li></ul><h2 id="技术特点" tabindex="-1">技术特点 <a class="header-anchor" href="#技术特点" aria-label="Permalink to &quot;技术特点&quot;">​</a></h2><h3 id="模块化设计" tabindex="-1">模块化设计 <a class="header-anchor" href="#模块化设计" aria-label="Permalink to &quot;模块化设计&quot;">​</a></h3><p>项目采用模块化架构，所有功能都作为独立模块实现，易于维护和扩展。</p><h3 id="现代化api" tabindex="-1">现代化API <a class="header-anchor" href="#现代化api" aria-label="Permalink to &quot;现代化API&quot;">​</a></h3><p>使用最新的Paper 1.21.11 API，确保兼容性和性能。</p><h3 id="灵活的gui系统" tabindex="-1">灵活的GUI系统 <a class="header-anchor" href="#灵活的gui系统" aria-label="Permalink to &quot;灵活的GUI系统&quot;">​</a></h3><ul><li>抽象GUI接口，易于创建各种界面</li><li>GUI管理器处理界面事件和状态</li><li>预设的任务GUI界面示例</li></ul><h3 id="完整的消息系统" tabindex="-1">完整的消息系统 <a class="header-anchor" href="#完整的消息系统" aria-label="Permalink to &quot;完整的消息系统&quot;">​</a></h3><ul><li>统一的消息工具类</li><li>支持ActionBar、聊天、BossBar等多种消息类型</li><li>颜色代码自动格式化</li></ul><h3 id="双数据库支持" tabindex="-1">双数据库支持 <a class="header-anchor" href="#双数据库支持" aria-label="Permalink to &quot;双数据库支持&quot;">​</a></h3><ul><li><strong>SQLite</strong>：默认数据库，无需额外配置，适合小型服务器</li><li><strong>MySQL</strong>：高性能数据库，适合大型服务器和集群环境</li><li><strong>自动配置</strong>：通过config.yml轻松切换数据库类型</li><li><strong>统一接口</strong>：使用相同的API操作不同类型的数据库</li></ul><h3 id="依赖管理" tabindex="-1">依赖管理 <a class="header-anchor" href="#依赖管理" aria-label="Permalink to &quot;依赖管理&quot;">​</a></h3><ul><li>自动检测并挂钩外部插件</li><li>安全的依赖使用机制</li><li>插件可用性检查</li></ul><h2 id="配置文件" tabindex="-1">配置文件 <a class="header-anchor" href="#配置文件" aria-label="Permalink to &quot;配置文件&quot;">​</a></h2><p>配置文件位于 <code>config.yml</code>，包含：</p><ul><li>基础设置（语言、调试模式）</li><li>数据库设置（类型、SQLite文件名、MySQL连接信息）</li><li>冒险等级系统配置</li><li>任务系统配置</li><li>新手教程配置</li><li>依赖挂钩设置</li></ul><h2 id="开发状态" tabindex="-1">开发状态 <a class="header-anchor" href="#开发状态" aria-label="Permalink to &quot;开发状态&quot;">​</a></h2><p>本插件已完成基础架构搭建，包括：</p><ul><li>✅ 模块化架构设计</li><li>✅ 依赖挂钩系统</li><li>✅ 命令系统</li><li>✅ GUI界面系统</li><li>✅ 消息系统（ActionBar、聊天、BossBar）</li><li>✅ 双数据库系统（SQLite/MySQL）</li><li>✅ 数据访问对象（DAO）层</li><li>✅ 配置管理系统</li><li>✅ 事件处理系统</li></ul>',46)])])}const f=l(t,[["render",r]]);export{b as __pageData,f as default};
