import{_ as d,e as l,b as o,c as i,d as a,h as r}from"./app.BAqqr1Gj.js";import"./chunks/vendor.8Zb1yHKv.js";import"./chunks/vendor-mermaid.6OwiGrWq.js";const q=JSON.parse('{"title":"锐界幻境 - 玩家工会系统","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"features/base/playerguild.md","filePath":"features/base/playerguild.md","lastUpdated":1771030499000}'),h={name:"features/base/playerguild.md"};function n(s,t,b,_,p,u){const e=l("SmartImage");return o(),i("div",null,[t[0]||(t[0]=a('<h1 id="锐界幻境-玩家工会系统" tabindex="-1">锐界幻境 - 玩家工会系统 <a class="header-anchor" href="#锐界幻境-玩家工会系统" aria-label="Permalink to &quot;锐界幻境 - 玩家工会系统&quot;">​</a></h1><blockquote><p>凝聚伙伴，共赴冒险 | 非核心玩法，主打轻松社交与集体体验</p></blockquote><h2 id="一、工会核心定位" tabindex="-1">一、工会核心定位 <a class="header-anchor" href="#一、工会核心定位" aria-label="Permalink to &quot;一、工会核心定位&quot;">​</a></h2><p>工会并非服务器核心玩法，而是为玩家群体打造的趣味组织形式——自定义工会名称、参与专属集体活动，轻松凝聚游戏伙伴。我们坚决抵制小团体专横跋扈的行为，倡导公平、友好、正向的工会氛围。</p><h2 id="二、工会创建规则" tabindex="-1">二、工会创建规则 <a class="header-anchor" href="#二、工会创建规则" aria-label="Permalink to &quot;二、工会创建规则&quot;">​</a></h2><p>创建工会需满足以下规则，建议创建前与服内辅助沟通确认：</p><table tabindex="0"><thead><tr><th>配置项</th><th>规则要求</th></tr></thead><tbody><tr><td>工会名称</td><td>字符数≤7，语言不限（支持原版颜色格式化码、特殊符号）</td></tr><tr><td>工会简介</td><td>可选填写，为个性化工会介绍，字数≤30</td></tr><tr><td>工会铭牌</td><td>可选配置，支持自行设计图片（宽度≤32像素，长度≤256像素，建议美观易识别）；也可使用纯文字形式，制作完成后需将图片发给服主协助导入（铭牌将用于工会名称展示、工会称号装饰）</td></tr></tbody></table>',7)),r(e,{src:"/images/features/tag.png",alt:"铭牌示例",caption:"铭牌示例",maxWidth:"100px"}),t[1]||(t[1]=a('<h2 id="三、工会管理与共创原则" tabindex="-1">三、工会管理与共创原则 <a class="header-anchor" href="#三、工会管理与共创原则" aria-label="Permalink to &quot;三、工会管理与共创原则&quot;">​</a></h2><p>我们对工会的发展秉持以下原则，保障服务器公平与体验：</p><ol><li><strong>互动共创</strong>：鼓励工会成员积极参与新服内容制作讨论，提出优化建议，共建优质服务器环境；</li><li><strong>玩法定位</strong>：工会并非服务器主推玩法，我们不抵制合规的工会形式，同时会为正常运营的工会提供适当支持；</li><li><strong>公平管控</strong>：严禁工会形成垄断、颠覆服务器平衡的行为，若出现此类问题，将采取恰当且必要的管控措施；</li><li><strong>价值导向</strong>：打破“工会=不良小团体”的刻板印象——工会是玩家团体的正向聚合形式，既提升成员凝聚力，也能通过积极沟通督促管理层优化服务，整体利大于弊。</li></ol><h2 id="四、工会系统入口" tabindex="-1">四、工会系统入口 <a class="header-anchor" href="#四、工会系统入口" aria-label="Permalink to &quot;四、工会系统入口&quot;">​</a></h2><p>所有工会相关操作均通过可视化 GUI 完成，操作路径如下：</p><ol><li>进入服务器后，输入指令 <code>/mc</code> 打开功能菜单；</li><li>点击菜单右上角【工会系统】按钮，进入工会操作界面</li></ol>',6)),r(e,{src:"/images/x.png",alt:"菜单里的工会系统按钮"}),t[2]||(t[2]=a('<ol start="3"><li>未加入任何工会时，界面左下角将显示【创建工会】按钮，点击后输入工会名，消耗灵叶（&amp;f&amp;a灵叶）或星痕石（&amp;f&amp;d星痕石）即可完成创建。</li></ol><h2 id="五、工会核心价值" tabindex="-1">五、工会核心价值 <a class="header-anchor" href="#五、工会核心价值" aria-label="Permalink to &quot;五、工会核心价值&quot;">​</a></h2><p>工会的核心作用是凝聚玩家群体，提升集体游戏体验：</p><ul><li>成员完成工会签到、贡献活跃度，可积累工会资金，助力工会等级提升；</li><li>等级提升后可解锁工会世界等专属功能，体验更多集体玩法；</li><li>形成正向的玩家社群，让游戏体验更具归属感。</li></ul><h2 id="六、工会核心功能详情" tabindex="-1">六、工会核心功能详情 <a class="header-anchor" href="#六、工会核心功能详情" aria-label="Permalink to &quot;六、工会核心功能详情&quot;">​</a></h2><h3 id="_6-1-基础管理功能" tabindex="-1">6.1 基础管理功能 <a class="header-anchor" href="#_6-1-基础管理功能" aria-label="Permalink to &quot;6.1 基础管理功能&quot;">​</a></h3><table tabindex="0"><thead><tr><th>功能名称</th><th>功能详情</th></tr></thead><tbody><tr><td>工会主城</td><td>会长可自定义设置游戏内任意位置作为工会主城，作为成员核心聚集点</td></tr><tr><td>工会传送</td><td>工会成员可通过专属功能一键传送至工会主城，无需手动跑图</td></tr><tr><td>工会成员管理</td><td>工会管理员可在 GUI 操作界面中，便捷完成成员添加/移除、权限调整、职位设置等操作</td></tr></tbody></table><h3 id="_6-2-福利与成长体系" tabindex="-1">6.2 福利与成长体系 <a class="header-anchor" href="#_6-2-福利与成长体系" aria-label="Permalink to &quot;6.2 福利与成长体系&quot;">​</a></h3><table tabindex="0"><thead><tr><th>功能名称</th><th>功能详情</th></tr></thead><tbody><tr><td>工会签到</td><td>成员每日完成签到可领取对应奖励，奖励等级与工会等级/活跃度直接挂钩</td></tr><tr><td>工会银行</td><td>成员贡献的物资/货币将统一转化为工会资金，用于工会升级、功能解锁等核心消耗</td></tr><tr><td>工会称号</td><td>称号随工会等级提升逐步解锁，支持自定义名称；解锁后附带专属 Buff 效果</td></tr><tr><td>工会神石</td><td>按服务器配置周期自动为工会增加活跃度，降低工会等级提升门槛</td></tr></tbody></table><h3 id="_6-3-专属场景功能" tabindex="-1">6.3 专属场景功能 <a class="header-anchor" href="#_6-3-专属场景功能" aria-label="Permalink to &quot;6.3 专属场景功能&quot;">​</a></h3><h4 id="工会世界" tabindex="-1">工会世界 <a class="header-anchor" href="#工会世界" aria-label="Permalink to &quot;工会世界&quot;">​</a></h4><p>若开启该功能，会长可消耗星痕石（&amp;f&amp;d星痕石）、灵叶（&amp;f&amp;a灵叶）或工会资金，创建带有边境限制的工会专属世界，供成员专属使用（如集体建造、资源采集等）。</p><h3 id="_6-4-竞技互动玩法" tabindex="-1">6.4 竞技互动玩法 <a class="header-anchor" href="#_6-4-竞技互动玩法" aria-label="Permalink to &quot;6.4 竞技互动玩法&quot;">​</a></h3><table tabindex="0"><thead><tr><th>功能名称</th><th>功能详情</th></tr></thead><tbody><tr><td>工会活动</td><td>目前开放两类工会战：<br>① 赛季工会战（周期性赛季制对抗，含赛季专属奖励）<br>② 匹配工会战（实时匹配其他工会进行对抗）</td></tr><tr><td>公平对决</td><td>支持开启“公平匹配/公平对决”模式，对战时自动平衡双方基础属性，保障竞技公平性</td></tr></tbody></table><h3 id="_6-5-交易流通功能" tabindex="-1">6.5 交易流通功能 <a class="header-anchor" href="#_6-5-交易流通功能" aria-label="Permalink to &quot;6.5 交易流通功能&quot;">​</a></h3><h4 id="工会商店" tabindex="-1">工会商店 <a class="header-anchor" href="#工会商店" aria-label="Permalink to &quot;工会商店&quot;">​</a></h4><p>工会成员可在专属商店内自主上架物品出售，供其他工会成员选购，实现工会内部物资高效流通与共享，提升资源利用率。</p>',17))])}const x=d(h,[["render",n]]);export{q as __pageData,x as default};
