import{_ as s,e as n,c as l,b as a,f as r,g as i,d as e,h as g}from"./app.PMs85RCR.js";import"./chunks/vendor.m4U5NtMm.js";import"./chunks/vendor-mermaid.dyYQKSZ5.js";const P=JSON.parse('{"title":"秋季 · 层林尽染","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"features/seasons/fall.md","filePath":"features/seasons/fall.md","lastUpdated":1767619713000}'),d={name:"features/seasons/fall.md"};function u(m,t,c,p,_,f){const o=n("SmartImage");return a(),l("div",null,[t[0]||(t[0]=r("h1",{id:"秋季-·-层林尽染",tabindex:"-1"},[g("秋季 · 层林尽染 "),r("a",{class:"header-anchor",href:"#秋季-·-层林尽染","aria-label":'Permalink to "秋季 · 层林尽染"'},"​")],-1)),i(o,{src:"/images/features/autumn.png",alt:"秋季景象",caption:"秋季 · 丹枫迎秋，暮云凝碧",width:"100",radius:"50px",shadow:"",border:"",lazy:!0}),t[1]||(t[1]=e('<p>秋意渐浓，<strong>白昼悄然缩短，骄阳的炽烈渐渐褪去</strong>。层林被染成斑斓的色彩，大地与河水蒙上了一层浑浊的棕黄，天空总是阴沉的灰色，仿佛随时都会有一场暴雨倾盆而下。狐狸、哞菇、青蛙、蝙蝠与（洞穴）蜘蛛开始在世界各地出没，秋日的森林，有着全年独一无二的绝美风光。</p><h2 id="秋季核心特色速览" tabindex="-1">秋季核心特色速览 <a class="header-anchor" href="#秋季核心特色速览" aria-label="Permalink to &quot;秋季核心特色速览&quot;">​</a></h2><ul><li><strong>默认开启时间</strong>：每年的 <strong>9月4日</strong>（具体以服务器日历系统为准）。</li><li><strong>视觉变化</strong>： <ul><li>树木的叶片幻化出<strong>橙、蓝、绿、黄、棕</strong>等五彩斑斓的色彩，层林尽染，美不胜收。</li><li>草地变成<strong>浅棕色的泥泞色调</strong>，带着秋日的萧瑟感。</li><li>天空被<strong>阴沉的灰色</strong>笼罩，氛围感拉满。</li><li>河水呈现出<strong>浑浊的棕黄色</strong>，仿佛饱含泥沙。</li></ul></li><li><strong>专属粒子</strong>：树木下方会持续飘落<strong>细小的秋叶粒子</strong>，行走在森林中，能清晰感受到秋意的降临。</li><li><strong>环境改造</strong>： <ul><li>世界各地会随机生成<strong>大片的蘑菇群</strong>，无论是红蘑菇还是棕蘑菇，都能轻松找到。</li><li><strong>降雨与雷暴变得极为频繁</strong>，出门冒险记得带上雨伞或搭建临时庇护所。</li><li>夏季生成的<strong>所有浆果丛会全部消失</strong>，完成季节的自然更替。</li></ul></li><li><strong>生物动态</strong>： <ul><li>蝙蝠不再局限于洞穴，<strong>夜晚会在地面上方自由生成</strong>。</li><li>哞菇、青蛙、狐狸这三种特色生物，<strong>会在世界的任何角落出现</strong>。</li><li>夜晚的蜘蛛生成数量大幅增加，<strong>甚至有概率直接生成洞穴蜘蛛</strong>，需格外小心。</li><li><strong>特殊彩蛋</strong>：所有生成的生物，都有 <strong>20% 的概率头顶南瓜</strong>，为秋日增添一份趣味。</li></ul></li><li><strong>昼夜长度</strong>：白天与夜晚的时长<strong>基本持平</strong>（具体以服务器昼夜长度配置为准）。</li><li><strong>温度区间</strong>：平均温度为 <strong>5℃ - 25℃</strong>，具体数值会根据你所在的生物群系有所差异。</li></ul><h2 id="秋季专属机制补充" tabindex="-1">秋季专属机制补充 <a class="header-anchor" href="#秋季专属机制补充" aria-label="Permalink to &quot;秋季专属机制补充&quot;">​</a></h2><ol><li><strong>五彩斑斓的林木视觉</strong>：秋季的核心视觉特色是树木的多彩变化，不同的树木、不同的生物群系，会呈现出不同的主色调，确保每一片森林都有独一无二的秋日风光，不会出现千篇一律的景象。</li><li><strong>地面蝙蝠的独特体验</strong>：秋季的蝙蝠会打破原版的生成限制，夜晚在地面的森林、平原、山地等区域都能看到它们的身影，为夜晚的冒险增添了一份别样的生机与趣味。</li><li><strong>生物头顶南瓜的趣味彩蛋</strong>：20% 的概率让生成的生物头顶南瓜，这一机制适用于所有生物——无论是温顺的牛羊、可爱的狐狸，还是凶猛的僵尸、骷髅，都有可能出现这一有趣的外观，你可以尝试收集各种头顶南瓜的生物，打造属于自己的秋日收藏。</li><li><strong>频繁降雨的环境影响</strong>：秋季的降雨与雷暴概率大幅提升，这不仅会影响你的冒险节奏，还会对温度产生一定的修正效果。同时，频繁的雷电也为下界合金的获取、苦力怕的高压变异等机制提供了更多的机会。</li></ol>',5))])}const S=s(d,[["render",u]]);export{P as __pageData,S as default};
