import{_ as o,e as n,b as l,c as e,f as r,g as i,h as a,d as g}from"./app.DiX3nUp-.js";import"./chunks/vendor.8Zb1yHKv.js";import"./chunks/vendor-mermaid.jBnhHIAT.js";const P=JSON.parse('{"title":"夏季 · 骄阳盛夏","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"features/seasons/summer.md","filePath":"features/seasons/summer.md","lastUpdated":1767619713000}'),m={name:"features/seasons/summer.md"};function d(u,t,c,p,_,h){const s=n("SmartImage");return l(),e("div",null,[t[0]||(t[0]=r("h1",{id:"夏季-·-骄阳盛夏",tabindex:"-1"},[i("夏季 · 骄阳盛夏 "),r("a",{class:"header-anchor",href:"#夏季-·-骄阳盛夏","aria-label":'Permalink to "夏季 · 骄阳盛夏"'},"​")],-1)),a(s,{src:"/images/features/summer.png",alt:"夏季景象",caption:"夏季 · 绿意盎然，星落长河",width:"100",radius:"50px",shadow:"",border:"",lazy:!0}),t[1]||(t[1]=g('<p>盛夏如期而至，<strong>白昼愈发漫长，骄阳炽热灼人</strong>。整个世界被浓郁的绿意包裹，仿佛化作一片广袤的丛林；原本只在丛林中出现的生物，如今遍布每一个角落；作物在充足的阳光下疯狂生长，气温也随之飙升至全年峰值。冒险前记得带上足够的水，才能更好地抵御酷暑的侵袭。</p><h2 id="夏季核心特色速览" tabindex="-1">夏季核心特色速览 <a class="header-anchor" href="#夏季核心特色速览" aria-label="Permalink to &quot;夏季核心特色速览&quot;">​</a></h2><ul><li><strong>默认开启时间</strong>：每年的 <strong>6月4日</strong>（具体以服务器日历系统为准）。</li><li><strong>视觉变化</strong>： <ul><li>大多数生物群系的树木与草皮，会呈现出类似丛林树叶的<strong>浓郁鲜绿色</strong>。</li><li>寒冷生物群系（积雪平原、针叶林等）的草皮，会变成平原的清新绿色。</li><li>炎热生物群系（沙漠、热带草原等）会呈现出<strong>略显干燥的视觉效果</strong>。</li><li>水面与天空会染上明亮的<strong>浅蓝滤镜</strong>。</li></ul></li><li><strong>专属粒子</strong>： <ul><li>夜晚的天空中可以观测到璀璨的<strong>流星</strong>，午夜前后是最佳观赏时机。</li><li>夜晚的森林里，成群的<strong>萤火虫</strong>依旧飞舞，点亮夜色。</li><li>白天，大片的<strong>树叶会从枝头缓缓飘落</strong>，营造出盛夏的生机。</li></ul></li><li><strong>环境改造</strong>： <ul><li>春季里自然生成的花朵会全部消失。</li><li>世界各地会随机生成一片片<strong>浆果丛</strong>，为你提供新鲜的食物来源。</li><li><strong>降雨变得极其罕见</strong>，整个夏季大多是晴朗的好天气。</li><li>无遮挡的作物生长加速：<strong>上方没有任何方块遮挡的作物/植物，生长速度会提升至原来的2倍</strong>。</li></ul></li><li><strong>生物动态</strong>： <ul><li>原本只在丛林生物群系中生成的动物，如今<strong>可以在世界的任何角落出现</strong>。</li><li>夜晚生成的僵尸会被<strong>尸壳完全替代</strong>，需注意它们的特殊攻击机制。</li></ul></li><li><strong>昼夜长度</strong>：<strong>昼长夜短</strong>成为常态，白天时长约 <strong>13分钟</strong>，夜晚仅约 <strong>7分钟</strong>（具体以服务器昼夜长度配置为准）。</li><li><strong>温度区间</strong>：平均温度为 <strong>25℃ - 40℃</strong>，具体数值会根据你所在的生物群系产生显著差异。</li></ul><h2 id="夏季专属机制补充" tabindex="-1">夏季专属机制补充 <a class="header-anchor" href="#夏季专属机制补充" aria-label="Permalink to &quot;夏季专属机制补充&quot;">​</a></h2><ol><li><strong>生物群系的差异化绿意</strong>：夏季的核心视觉特色是浓郁的绿色，但为了保证世界的多样性，不同生物群系的表现各不相同。寒冷生物群系会褪去冬日的雪白，换上温和的平原绿；炎热生物群系则会在绿意中透出干燥的质感，不会显得过于违和。</li><li><strong>无遮挡作物加速生长</strong>：这是夏季最实用的机制之一。只要你的农田上方没有搭建顶棚（玻璃、树叶、木板等任何方块），作物就能享受充足的阳光，以2倍速快速成熟。无论是小麦、胡萝卜，还是南瓜、西瓜，都能从中受益。</li><li><strong>流星与落叶的双重沉浸</strong>：白天，树叶纷飞为森林增添生机；夜晚，流星划过天空为冒险增添浪漫。这两种专属粒子效果，让夏季的昼夜都拥有了独一无二的辨识度。</li></ol>',5))])}const S=o(m,[["render",d]]);export{P as __pageData,S as default};
