import{_ as a,b as i,c as e,d as r}from"./app.BDlP2o-V.js";import"./chunks/vendor.8Zb1yHKv.js";import"./chunks/vendor-mermaid.CX8s52fm.js";const b=JSON.parse('{"title":"玩家行为守则","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"manual/eula.md","filePath":"manual/eula.md","lastUpdated":1765778806000}'),o={name:"manual/eula.md"};function t(h,l,n,d,s,u){return i(),e("div",null,[...l[0]||(l[0]=[r('<h1 id="玩家行为守则" tabindex="-1">玩家行为守则 <a class="header-anchor" href="#玩家行为守则" aria-label="Permalink to &quot;玩家行为守则&quot;">​</a></h1><h3 id="阅读提示" tabindex="-1">阅读提示 <a class="header-anchor" href="#阅读提示" aria-label="Permalink to &quot;阅读提示&quot;">​</a></h3><p>本守则适用于锐界幻境MC生存服务器内所有游戏行为，以及官方QQ交流群的所有交流行为，是所有玩家、群成员必须遵守的基本准则，管理员将依据本守则公平处理违规行为。</p><hr><h2 id="一、游戏内行为规范" tabindex="-1">一、游戏内行为规范 <a class="header-anchor" href="#一、游戏内行为规范" aria-label="Permalink to &quot;一、游戏内行为规范&quot;">​</a></h2><h3 id="_1-1-聊天与内容发布规范" tabindex="-1">1.1 聊天与内容发布规范 <a class="header-anchor" href="#_1-1-聊天与内容发布规范" aria-label="Permalink to &quot;1.1 聊天与内容发布规范&quot;">​</a></h3><details open><summary>点击展开/收起规则</summary><h4 id="禁止发布-使用的内容" tabindex="-1">禁止发布/使用的内容 <a class="header-anchor" href="#禁止发布-使用的内容" aria-label="Permalink to &quot;禁止发布/使用的内容&quot;">​</a></h4><ol><li>涉及政治、色情、暴力、恐怖的文字、图像及相关衍生内容，包括但不限于： <ul><li>游戏内告示牌、书籍的文字内容</li><li>玩家皮肤、建筑外观的图案设计</li><li>物品自定义命名、聊天发言</li></ul></li><li>纳粹符号、歧视性言论（地域、性别、民族、宗教等）</li><li>其他服务器的宣传、拉人、对比类内容</li></ol><h4 id="限制行为" tabindex="-1">限制行为 <a class="header-anchor" href="#限制行为" aria-label="Permalink to &quot;限制行为&quot;">​</a></h4><ol><li>禁止无意义刷屏（连续发送5条及以上相同/重复消息）</li><li>禁止散布虚假谣言（如服务器关闭、管理员不公等）</li><li>禁止泄露他人游戏ID、现实隐私（手机号、住址、照片等）</li><li>禁止恶意引战、钓鱼（故意挑起玩家矛盾、欺骗他人物资等）</li></ol><h4 id="沟通建议" tabindex="-1">沟通建议 <a class="header-anchor" href="#沟通建议" aria-label="Permalink to &quot;沟通建议&quot;">​</a></h4><ol><li>对服务器的建议、投诉请私聊管理员 @F.windEmiko</li><li>与其他玩家产生争议时，优先私聊沟通，避免公开争吵</li><li>共同维护服务器友好的游戏氛围，文明交流</li></ol></details><h3 id="_1-2-核心游戏行为规范" tabindex="-1">1.2 核心游戏行为规范 <a class="header-anchor" href="#_1-2-核心游戏行为规范" aria-label="Permalink to &quot;1.2 核心游戏行为规范&quot;">​</a></h3><details open><summary>点击展开/收起规则</summary><h4 id="禁止行为-违规将直接封禁" tabindex="-1">禁止行为（违规将直接封禁） <a class="header-anchor" href="#禁止行为-违规将直接封禁" aria-label="Permalink to &quot;禁止行为（违规将直接封禁）&quot;">​</a></h4><ol><li>作弊相关： <ul><li>使用飞行、加速、穿墙等作弊外挂/模组</li><li>启用矿透（X-ray）、生物雷达等透视类工具</li><li>运行自动挖矿、自动打怪、挂机刷物等脚本/插件</li><li>使用修改版作弊客户端（如添加非法功能的整合包）</li></ul></li><li>财产与权益侵犯： <ul><li>盗取其他玩家的物资、装备、建筑</li><li>恶意破坏他人建筑、农田、红石机器、养殖生物</li><li>未经允许击杀他人生存用生物（如村民、家畜）</li><li>对他人基地进行劫掠、放置陷阱等恶意攻击</li></ul></li><li>服务器环境破坏： <ul><li>放置高频红石、卡服机器导致服务器卡顿/崩溃</li><li>在主城、公共区域滥用爆炸物（TNT、苦力怕等）破坏环境</li><li>恶意制造区块卡顿、刷怪塔泛滥等影响服务器运行的行为</li></ul></li></ol></details><h3 id="_1-3-机器-设施使用规范" tabindex="-1">1.3 机器/设施使用规范 <a class="header-anchor" href="#_1-3-机器-设施使用规范" aria-label="Permalink to &quot;1.3 机器/设施使用规范&quot;">​</a></h3><details open><summary>点击展开/收起规则</summary><h4 id="核心限制要求" tabindex="-1">核心限制要求 <a class="header-anchor" href="#核心限制要求" aria-label="Permalink to &quot;核心限制要求&quot;">​</a></h4><ol><li>跨维度（主世界/下界/末地）的大型红石机器、刷物机需满足： <ul><li>运行时必须有玩家值守，禁止无人看管的挂机机器</li><li>需配置产物自动销毁/限制机制，避免物资泛滥</li><li>严禁使用区块加载器（无论是否付费）强制加载区块</li></ul></li><li>公共区域设施： <ul><li>禁止占用公共资源搭建私人大型机器</li><li>使用公共传送、公共农田等设施后需保持整洁，禁止破坏</li></ul></li></ol></details><hr><h2 id="二、qq交流群行为规范" tabindex="-1">二、QQ交流群行为规范 <a class="header-anchor" href="#二、qq交流群行为规范" aria-label="Permalink to &quot;二、QQ交流群行为规范&quot;">​</a></h2><h3 id="_2-1-发言与内容发布规范" tabindex="-1">2.1 发言与内容发布规范 <a class="header-anchor" href="#_2-1-发言与内容发布规范" aria-label="Permalink to &quot;2.1 发言与内容发布规范&quot;">​</a></h3><details open><summary>点击展开/收起规则</summary><h4 id="禁止发布-使用的内容-1" tabindex="-1">禁止发布/使用的内容 <a class="header-anchor" href="#禁止发布-使用的内容-1" aria-label="Permalink to &quot;禁止发布/使用的内容&quot;">​</a></h4><ol><li>涉及政治、色情、暴力、恐怖的文字、图片、视频、链接等</li><li>纳粹相关符号、歧视性言论（地域、性别、民族等）</li><li>低俗、恶心、血腥的图片/视频（如“搬屎发屎”类内容）</li></ol><h4 id="限制行为-1" tabindex="-1">限制行为 <a class="header-anchor" href="#限制行为-1" aria-label="Permalink to &quot;限制行为&quot;">​</a></h4><ol><li>禁止无意义刷屏（连续发送5条及以上相同消息）</li><li>禁止泄露群成员的QQ号、手机号、照片等隐私信息</li><li>禁止恶意钓鱼、引战（故意挑起矛盾、欺骗他人等）</li><li>禁止宣传其他MC服务器、QQ群、微信群，或私自拉人建群</li><li>禁止发布未经管理员允许的广告、砍价、投票、拼单等链接</li><li>禁止散布关于服务器、群聊的虚假谣言</li></ol><h4 id="沟通建议-1" tabindex="-1">沟通建议 <a class="header-anchor" href="#沟通建议-1" aria-label="Permalink to &quot;沟通建议&quot;">​</a></h4><ol><li>群内交流优先围绕MC游戏相关内容，保持话题积极健康</li><li>有群管理相关建议可私聊群主/管理员，理性反馈</li><li>维护群聊整洁，共同营造友好的交流环境</li></ol></details><h3 id="_2-2-群内行为规范" tabindex="-1">2.2 群内行为规范 <a class="header-anchor" href="#_2-2-群内行为规范" aria-label="Permalink to &quot;2.2 群内行为规范&quot;">​</a></h3><details open><summary>点击展开/收起规则</summary><h4 id="禁止行为-违规将移出-封禁群聊" tabindex="-1">禁止行为（违规将移出/封禁群聊） <a class="header-anchor" href="#禁止行为-违规将移出-封禁群聊" aria-label="Permalink to &quot;禁止行为（违规将移出/封禁群聊）&quot;">​</a></h4><ol><li>账号安全与骚扰类： <ul><li>盗用他人QQ账号在群内发言、发布违规内容</li><li>利用漏洞批量添加群成员为好友，或频繁私信骚扰群成员</li><li>恶意举报群成员、群聊，干扰群正常运行</li></ul></li><li>群管理破坏类： <ul><li>私自修改群昵称模仿管理员，误导其他群成员</li><li>未经允许修改群公告、群相册、群文件等内容</li><li>恶意踢除、举报群成员（仅限群管理员，普通成员禁止恶意举报）</li></ul></li></ol><h4 id="群活动规范" tabindex="-1">群活动规范 <a class="header-anchor" href="#群活动规范" aria-label="Permalink to &quot;群活动规范&quot;">​</a></h4><ol><li>发起群投票、群活动（如游戏组队、线下交流等）需提前向管理员报备</li><li>群活动内容需积极健康、合法合规，不得涉及商业盈利目的</li><li>禁止利用群活动进行诈骗、拉人头等违规行为</li></ol></details><hr><h2 id="三、违规处理说明" tabindex="-1">三、违规处理说明 <a class="header-anchor" href="#三、违规处理说明" aria-label="Permalink to &quot;三、违规处理说明&quot;">​</a></h2><ol><li>违规行为将根据情节轻重，采取警告、临时禁言/封服、永久封禁（游戏/群聊）等处理方式；</li><li>对违规处理有异议的，可向管理员提交书面申诉，管理员将在2个工作日内反馈；</li><li>管理员有权根据服务器运营需要，对本守则进行补充或调整，调整后将在服务器和群内公示。</li></ol>',20)])])}const _=a(o,[["render",t]]);export{b as __pageData,_ as default};
