import{_ as a,b as r,c as e,d}from"./app.BAqqr1Gj.js";import"./chunks/vendor.8Zb1yHKv.js";import"./chunks/vendor-mermaid.6OwiGrWq.js";const _=JSON.parse('{"title":"锐界幻境 MC生存服务器 - 入服审核说明","description":"","frontmatter":{"contributors":"F.windEmiko,FwindEmi86"},"headers":[],"relativePath":"manual/review.md","filePath":"manual/review.md","lastUpdated":1771031097000}'),l={name:"manual/review.md"};function i(o,t,h,n,s,c){return r(),e("div",null,[...t[0]||(t[0]=[d('<h1 id="锐界幻境-mc生存服务器-入服审核说明" tabindex="-1">锐界幻境 MC生存服务器 - 入服审核说明 <a class="header-anchor" href="#锐界幻境-mc生存服务器-入服审核说明" aria-label="Permalink to &quot;锐界幻境 MC生存服务器 - 入服审核说明&quot;">​</a></h1><p>我们致力于打造友好、稳定的慢节奏生存社区，入服审核旨在过滤不良行为玩家（如熊孩子），保障所有玩家的游戏体验，以下是具体的审核规则与流程。</p><hr><h2 id="一、入服申请流程" tabindex="-1">一、入服申请流程 <a class="header-anchor" href="#一、入服申请流程" aria-label="Permalink to &quot;一、入服申请流程&quot;">​</a></h2><h3 id="_1-填写申请表" tabindex="-1">1. 填写申请表 <a class="header-anchor" href="#_1-填写申请表" aria-label="Permalink to &quot;1. 填写申请表&quot;">​</a></h3><p>请通过<a href="https://qm.qq.com/q/NG4owyVUyc" target="_blank" rel="noreferrer">入服申请表</a>填写以下核心信息（确保真实，便于审核）：</p><ul><li>游戏年龄（玩MC的时长）及擅长的玩法（如生存、建筑、红石等）；</li><li>对本服务器“慢节奏生存+友好社交”定位的理解；</li><li>希望在服务器中获得的游戏体验（如组队生存、创意建造等）。</li></ul><h3 id="_2-审核与反馈" tabindex="-1">2. 审核与反馈 <a class="header-anchor" href="#_2-审核与反馈" aria-label="Permalink to &quot;2. 审核与反馈&quot;">​</a></h3><ul><li>审核时效：我们会在3个工作日内通过QQ告知审核结果；</li><li>未收到反馈：若超时未收到回复，可联系服主@狐风轩汐核实。</li></ul><hr><h2 id="二、审核核心标准" tabindex="-1">二、审核核心标准 <a class="header-anchor" href="#二、审核核心标准" aria-label="Permalink to &quot;二、审核核心标准&quot;">​</a></h2><h3 id="🌸-我们欢迎这样的玩家" tabindex="-1">🌸 我们欢迎这样的玩家 <a class="header-anchor" href="#🌸-我们欢迎这样的玩家" aria-label="Permalink to &quot;🌸 我们欢迎这样的玩家&quot;">​</a></h3><table tabindex="0"><thead><tr><th>特质</th><th>具体说明</th></tr></thead><tbody><tr><td>遵守规则</td><td>认可并遵守服务器社区规则，不破坏、不偷取、不骚扰他人</td></tr><tr><td>热爱生存/创造</td><td>享受慢节奏生存的乐趣，愿意参与社区建造或组队协作</td></tr><tr><td>友好沟通</td><td>与其他玩家交流礼貌理性，拒绝辱骂、抬杠等恶意行为</td></tr><tr><td>长期稳定参与</td><td>把服务器当作长期游玩的平台，而非短期薅取资源就离开</td></tr><tr><td>理性处事</td><td>遇到问题优先沟通解决，不情绪化攻击他人或破坏环境</td></tr></tbody></table><h3 id="🚫-我们拒绝的行为-特质" tabindex="-1">🚫 我们拒绝的行为/特质 <a class="header-anchor" href="#🚫-我们拒绝的行为-特质" aria-label="Permalink to &quot;🚫 我们拒绝的行为/特质&quot;">​</a></h3><table tabindex="0"><thead><tr><th>特质</th><th>具体说明</th></tr></thead><tbody><tr><td>熊行为倾向</td><td>有破坏他人建筑、偷取物资、恶意刷屏/炸服等行为记录</td></tr><tr><td>言语不文明</td><td>习惯性辱骂、嘲讽、歧视其他玩家，破坏社区氛围</td></tr><tr><td>依赖索取</td><td>不主动生存，一味向他人索要物资，拒绝协作付出</td></tr><tr><td>短期薅资源</td><td>仅想短暂游玩、薅取资源后离开，无长期参与意愿</td></tr><tr><td>情绪失控</td><td>因小事情绪化发泄，如辱骂管理员、破坏公共建筑等</td></tr></tbody></table><hr><h2 id="三、服务器核心特色-供你参考匹配" tabindex="-1">三、服务器核心特色（供你参考匹配） <a class="header-anchor" href="#三、服务器核心特色-供你参考匹配" aria-label="Permalink to &quot;三、服务器核心特色（供你参考匹配）&quot;">​</a></h2><p>若你期待以下游戏体验，本服务器或许适合你： ✅ 无强制任务，纯慢节奏生存玩法，拒绝快餐化体验； ✅ 重视玩家间的友好社交，鼓励组队生存、互相帮助； ✅ 开放创意建造区域，支持玩家发挥脑洞打造专属建筑； ✅ 管理员公平管理，及时处理熊行为、纠纷等问题。</p><hr><h2 id="四、补充说明" tabindex="-1">四、补充说明 <a class="header-anchor" href="#四、补充说明" aria-label="Permalink to &quot;四、补充说明&quot;">​</a></h2><ol><li>审核结果仅作为入服门槛，入服后若出现上述“拒绝的行为”，仍会按规则处理（警告/踢服/封禁）；</li><li>申请表信息需真实，若发现隐瞒“熊行为”记录，审核直接不通过；</li><li>我们尊重每位玩家的游戏习惯，只要遵守规则，无论新手/老手都能找到属于自己的玩法。</li></ol><p>希望能在锐界幻境与你相遇，共同打造温馨的生存社区～</p>',22)])])}const p=a(l,[["render",i]]);export{_ as __pageData,p as default};
